public sealed class MyEffectSettings : EffectSettingsBase
{
    private readonly MyEffect _effect;

    public MyEffectSettings(MyEffect effect) => _effect = effect;

    public override string DisplayName => "My Effect";
    public override string Description => "Does something cool.";

    public override IEnumerable<IToolOption> GetOptions()
    {
        yield return new SliderOption(
            "amount", "Amount", 0, 100, _effect.Amount,
            v => _effect.Amount = (int)v,
            Order: 0, Tooltip: "Effect intensity");
    }
}

